close allclear allclcformat longa1 = 10;a2 = 13;a3 = 8;a4 = 10;a1_a2 = a1/a2;a1_a4 = a1/a4;c = ((a1^2+a2^2+a4^2)-a3^2)/(2*a2*a4);ejex = @(x) 0*x;x0_1 = -0.1;x0_2 = 2*pi/3;tol = 10^(-5);nmax = 1000;for beta = [0:0.1:2*pi/3]  f = @(alpha) a1_a2.*cos(beta) - a1_a4.*cos(alpha) - cos(beta-alpha) + c;  df = @(alpha) a1_a4.*sin(alpha) - sin(beta-alpha);  [zero0, res0, niter0] = newton(f,df,x0_1,tol,nmax)  [zeroPI, resPI, niterPI] = newton(f,df,x0_2,tol,nmax)endfor